from pydantic import BaseModel, validator

class TranslationUnit(BaseModel):
    source_content: str
    target_lang: str

    @validator('target_lang')
    def validate_language_code(cls, v):
        if len(v) != 2:
            raise ValueError('Language code must be 2 characters')
        return v.lower()

